import React, { useState, useEffect } from 'react';
import './MeetingRecordingModal.css';

interface MeetingRecordingModalProps {
  isOpen: boolean;
  onClose: () => void;
  meetingName: string;
  agendaItems: any[];
}

interface ScriptItem {
  id: string;
  speaker: string;
  content: string;
  timestamp: Date;
}

interface MeetingRecordItem {
  id: string;
  agendaName: string;
  discussion: string;
  type: 'comment' | 'instruction';
  department: string;
}

const MeetingRecordingModal: React.FC<MeetingRecordingModalProps> = ({
  isOpen,
  onClose,
  meetingName,
  agendaItems
}) => {
  const [activeTab, setActiveTab] = useState<'script' | 'record'>('script');
  const [recordingStates, setRecordingStates] = useState<{ [key: string]: boolean }>({});
  const [scriptItems, setScriptItems] = useState<ScriptItem[]>([]);
  const [meetingRecords, setMeetingRecords] = useState<MeetingRecordItem[]>([]);
  const [editingRecord, setEditingRecord] = useState<string | null>(null);

  // 녹음 상태 토글
  const toggleRecording = (agendaId: string) => {
    setRecordingStates(prev => ({
      ...prev,
      [agendaId]: !prev[agendaId]
    }));

    // 녹음 시작 시 더미 스크립트 추가 (실제로는 음성 인식 API 연동)
    if (!recordingStates[agendaId]) {
      const newScriptItem: ScriptItem = {
        id: Date.now().toString(),
        speaker: '김철수',
        content: '안녕하세요. 오늘 회의를 시작하겠습니다.',
        timestamp: new Date()
      };
      setScriptItems(prev => [...prev, newScriptItem]);
    }
  };

  // 회의록 자동 생성 (스크립트 기반)
  useEffect(() => {
    if (scriptItems.length > 0) {
      const records: MeetingRecordItem[] = agendaItems.map((agenda, index) => ({
        id: `record-${index}`,
        agendaName: agenda.title,
        discussion: scriptItems
          .filter(item => item.speaker)
          .map(item => `${item.speaker}: ${item.content}`)
          .join('\n'),
        type: 'comment' as const,
        department: agenda.presenter || '미정'
      }));
      setMeetingRecords(records);
    }
  }, [scriptItems, agendaItems]);

  // 회의록 항목 수정
  const handleRecordEdit = (recordId: string, field: string, value: string) => {
    setMeetingRecords(prev => prev.map(record => 
      record.id === recordId 
        ? { ...record, [field]: value }
        : record
    ));
  };

  // 회의록 항목 삭제
  const handleRecordDelete = (recordId: string) => {
    if (window.confirm('이 항목을 삭제하시겠습니까?')) {
      setMeetingRecords(prev => prev.filter(record => record.id !== recordId));
    }
  };

  // 회의록 저장
  const handleSaveRecords = () => {
    // 실제로는 API 호출
    console.log('회의록 저장:', meetingRecords);
    alert('회의록이 저장되었습니다.');
  };

  // 회의록 내보내기
  const handleExportRecords = () => {
    // 실제로는 PDF 또는 Excel 내보내기
    console.log('회의록 내보내기:', meetingRecords);
    alert('회의록이 내보내기되었습니다.');
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay recording-modal-overlay">
      <div className="recording-modal-container">
        {/* 회의명 헤더 */}
        <div className="meeting-header">
          <h1 className="meeting-title">{meetingName}</h1>
          <button className="btn btn-cancel" onClick={onClose}>
            <i className="fas fa-times"></i>
          </button>
        </div>

        {/* 탭 메뉴 */}
        <div className="recording-tabs">
          <button 
            className={`recording-tab ${activeTab === 'script' ? 'active' : ''}`}
            onClick={() => setActiveTab('script')}
          >
            <i className="fas fa-microphone"></i>
            회의 내용 스크립트
          </button>
          <button 
            className={`recording-tab ${activeTab === 'record' ? 'active' : ''}`}
            onClick={() => setActiveTab('record')}
          >
            <i className="fas fa-file-alt"></i>
            회의록
          </button>
        </div>

        {/* 탭 컨텐츠 */}
        <div className="recording-content">
          {activeTab === 'script' ? (
            // 회의 내용 스크립트 탭
            <div className="script-tab">
              <div className="agenda-script-list">
                {agendaItems.map((agenda, index) => (
                  <div key={agenda.id} className="agenda-script-card">
                    <div className="agenda-script-header">
                      <div className="agenda-info">
                        <h3>{index + 1}. {agenda.title}</h3>
                        <div className="agenda-meta">
                          <span className="duration">
                            <i className="fas fa-clock"></i>
                            {agenda.duration || '시간 미정'}
                          </span>
                          <span className="presenter">
                            <i className="fas fa-user"></i>
                            {agenda.presenter || '보고자 미정'}
                          </span>
                        </div>
                      </div>
                      <div className="recording-controls">
                        <button
                          className={`btn ${recordingStates[agenda.id] ? 'btn-danger' : 'btn-primary'}`}
                          onClick={() => toggleRecording(agenda.id)}
                        >
                          <i className={`fas ${recordingStates[agenda.id] ? 'fa-stop' : 'fa-microphone'}`}></i>
                          {recordingStates[agenda.id] ? '정지' : '녹음'}
                        </button>
                        {recordingStates[agenda.id] && (
                          <span className="recording-status">
                            <i className="fas fa-circle"></i>
                            녹음 중
                          </span>
                        )}
                      </div>
                    </div>
                    
                    <div className="script-content">
                      {scriptItems.length > 0 ? (
                        <div className="script-items">
                          {scriptItems.map((item) => (
                            <div key={item.id} className="script-item">
                              <div className="speaker-info">
                                <div className="speaker-avatar">
                                  <i className="fas fa-user"></i>
                                </div>
                                <span className="speaker-name">{item.speaker}</span>
                                <span className="timestamp">
                                  {item.timestamp.toLocaleTimeString()}
                                </span>
                              </div>
                              <div className="script-text">
                                {item.content}
                              </div>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="empty-script">
                          <i className="fas fa-microphone-slash"></i>
                          <p>녹음을 시작하면 실시간 스크립트가 표시됩니다.</p>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            // 회의록 탭
            <div className="record-tab">
              <div className="record-header">
                <h3>회의록</h3>
                <div className="record-actions">
                  <button className="btn btn-primary" onClick={handleSaveRecords}>
                    <i className="fas fa-save"></i>
                    저장
                  </button>
                  <button className="btn btn-secondary" onClick={handleExportRecords}>
                    <i className="fas fa-download"></i>
                    내보내기
                  </button>
                </div>
              </div>

              <div className="record-table-container">
                <table className="record-table">
                  <thead>
                    <tr>
                      <th>Agenda명</th>
                      <th>논의 내용</th>
                      <th>구분</th>
                      <th>담당 부서</th>
                      <th>작업</th>
                    </tr>
                  </thead>
                  <tbody>
                    {meetingRecords.map((record) => (
                      <tr 
                        key={record.id} 
                        className={`record-row ${record.type === 'instruction' ? 'instruction-row' : ''}`}
                      >
                        <td>{record.agendaName}</td>
                        <td>
                          {editingRecord === record.id ? (
                            <textarea
                              className="edit-textarea"
                              value={record.discussion}
                              onChange={(e) => handleRecordEdit(record.id, 'discussion', e.target.value)}
                              onBlur={() => setEditingRecord(null)}
                            />
                          ) : (
                            <div className="discussion-content" onClick={() => setEditingRecord(record.id)}>
                              {record.discussion}
                            </div>
                          )}
                        </td>
                        <td>
                          <select
                            className="type-select"
                            value={record.type}
                            onChange={(e) => handleRecordEdit(record.id, 'type', e.target.value)}
                          >
                            <option value="comment">Comment</option>
                            <option value="instruction">지시사항</option>
                          </select>
                        </td>
                        <td>
                          <input
                            type="text"
                            className="department-input"
                            value={record.department}
                            onChange={(e) => handleRecordEdit(record.id, 'department', e.target.value)}
                          />
                        </td>
                        <td>
                          <div className="record-actions-cell">
                            <button
                              className="btn btn-secondary btn-sm"
                              onClick={() => setEditingRecord(record.id)}
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button
                              className="btn btn-danger btn-sm"
                              onClick={() => handleRecordDelete(record.id)}
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MeetingRecordingModal;
