  // AI 화자 구분 함수
  const analyzeVoiceSignature = (audioData: any) => {
    // 실제 구현에서는 음성 분석 API를 사용
    // 여기서는 시뮬레이션으로 구현
    const voiceFeatures = {
      pitch: Math.random() * 100 + 100, // 음높이
      tempo: Math.random() * 50 + 80,   // 말하기 속도
      volume: Math.random() * 30 + 70,  // 볼륨
      accent: Math.random() > 0.5 ? 'standard' : 'regional' // 억양
