import React, { useState, useEffect } from 'react';
import { Meeting } from '../types/Meeting';
import './MeetingTable.css';

interface MeetingTableProps {
  meetings: Meeting[];
  selectedMeetings: string[];
  onMeetingSelect: (meetingId: string, isSelected: boolean) => void;
  onWebexJoin: (meeting: Meeting) => void;
  onMinutesAction: (meeting: Meeting) => void;
  onMeetingEdit: (meeting: Meeting) => void; // 회의 편집 함수 추가
  onMinutesEdit: (meeting: Meeting) => void; // 회의록 편집 함수 추가
}

const MeetingTable: React.FC<MeetingTableProps> = ({
  meetings,
  selectedMeetings,
  onMeetingSelect,
  onWebexJoin,
  onMinutesAction,
  onMeetingEdit,
  onMinutesEdit
}) => {
  const [currentTime, setCurrentTime] = useState(new Date());

  // 실시간으로 현재 시간 업데이트 (1분마다)
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // 1분마다 업데이트

    return () => clearInterval(timer);
  }, []);

  // 회의 상태를 동적으로 계산하는 함수
  const calculateMeetingStatus = (meeting: Meeting): 'scheduled' | 'in-progress' | 'completed' => {
    // startDateTime과 endDateTime이 있으면 그것을 사용
    if (meeting.startDateTime && meeting.endDateTime) {
      if (currentTime < meeting.startDateTime) {
        return 'scheduled';
      } else if (currentTime >= meeting.startDateTime && currentTime <= meeting.endDateTime) {
        return 'in-progress';
      } else {
        return 'completed';
      }
    }

    // 기존 status를 사용 (fallback)
    return meeting.status;
  };

  // 회의록 상태를 동적으로 계산하는 함수
  const calculateMinutesStatus = (meeting: Meeting): 'write' | 'view' | 'draft' => {
    // 현재는 minutesStatus를 그대로 사용하지만, 
    // 나중에 내보내기 버튼 클릭 시 'view'로 변경될 수 있음
    return meeting.minutesStatus;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled':
        return 'status-scheduled';
      case 'in-progress':
        return 'status-in-progress';
      case 'completed':
        return 'status-completed';
      default:
        return '';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'scheduled':
        return '예정';
      case 'in-progress':
        return '진행중';
      case 'completed':
        return '완료';
      default:
        return status;
    }
  };

  const getMinutesButtonText = (status: string) => {
    switch (status) {
      case 'write':
        return '작성중';
      case 'draft':
        return '작성중';
      case 'view':
        return '보기';
      default:
        return '작성중';
    }
  };

  const getMinutesButtonIcon = (status: string) => {
    switch (status) {
      case 'write':
      case 'draft':
        return 'fas fa-pencil-alt';
      case 'view':
        return 'fas fa-eye';
      default:
        return 'fas fa-pencil-alt';
    }
  };

  const handleMinutesClick = (meeting: Meeting) => {
    if (meeting.minutesStatus === 'view') {
      // 완료된 회의록은 보기만 가능
      onMinutesAction(meeting);
    } else {
      // 작성중이거나 임시 저장된 회의록은 편집 가능
      onMinutesEdit(meeting);
    }
  };

  const formatAttendees = (attendees: string[], total: number) => {
    if (attendees.length === 0) return '';
    const displayAttendees = attendees.slice(0, 3);
    const remaining = total - attendees.length;
    if (remaining > 0) {
      return `${displayAttendees.join(', ')} 외 ${remaining}명`;
    }
    return displayAttendees.join(', ');
  };

  return (
    <div className="table-container">
      <table className="meeting-table">
        <thead>
          <tr>
            <th className="checkbox-header"></th>
            <th>회의명</th>
            <th>일시</th>
            <th>장소</th>
            <th>주관자</th>
            <th>참석자</th>
            <th>WebEx</th>
            <th>상태</th>
            <th>회의록</th>
          </tr>
        </thead>
        <tbody>
          {meetings.length === 0 ? (
            <tr>
              <td colSpan={9} className="empty-table">
                <div className="empty-state">
                  <i className="fas fa-calendar-times"></i>
                  <h3>등록된 회의가 없습니다</h3>
                  <p>회의 등록 / 기록 버튼을 클릭하여 첫 번째 회의를 등록해보세요.</p>
                </div>
              </td>
            </tr>
          ) : (
            meetings.map((meeting) => {
              const currentStatus = calculateMeetingStatus(meeting);
              const currentMinutesStatus = calculateMinutesStatus(meeting);

              return (
                <tr key={meeting.id}>
                  <td className="checkbox-cell">
                    <input
                      type="checkbox"
                      checked={selectedMeetings.includes(meeting.id)}
                      onChange={(e) => onMeetingSelect(meeting.id, e.target.checked)}
                    />
                  </td>
                  <td className="meeting-name">
                    <div className="meeting-name-container">
                      <span 
                        className="meeting-name-clickable"
                        onClick={() => onMeetingEdit(meeting)}
                        title="회의 정보 편집"
                      >
                        {meeting.name}
                      </span>
                    </div>
                  </td>
                  <td className="meeting-datetime">
                    <div className="datetime-info">
                      <span>{meeting.date}</span>
                    </div>
                    <div className="datetime-info">
                      <span>{meeting.time}</span>
                    </div>
                  </td>
                  <td className="meeting-location">{meeting.location}</td>
                  <td className="meeting-organizer">
                    {meeting.organizer.name}
                  </td>
                  <td className="meeting-attendees">
                    {formatAttendees(meeting.attendees, meeting.totalAttendees)}
                  </td>
                  <td className="meeting-webex">
                    {meeting.webexUrl ? (
                      <div className="webex-icon" onClick={() => onWebexJoin(meeting)}>
                        <i className="fas fa-video"></i>
                      </div>
                    ) : (
                      <div className="no-webex">
                        <span>-</span>
                      </div>
                    )}
                  </td>
                  <td className="meeting-status">
                    <span className={`status-badge ${getStatusColor(currentStatus)}`}>
                      {getStatusText(currentStatus)}
                    </span>
                  </td>
                  <td className="meeting-minutes">
                    <button
                      onClick={() => handleMinutesClick(meeting)}
                      className={`minutes-button ${currentMinutesStatus === 'write' ? 'write' : 'view'}`}
                    >
                      <i className={getMinutesButtonIcon(currentMinutesStatus)}></i>
                      <span>{getMinutesButtonText(currentMinutesStatus)}</span>
                    </button>
                  </td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>
    </div>
  );
};

export default MeetingTable;
