import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import ActionButtons from './components/ActionButtons';
import MeetingTable from './components/MeetingTable';
import { Meeting } from './types/Meeting';
import { meetings as initialMeetings } from './data/meetings';
import './App.css';

const App: React.FC = () => {
  const [meetings] = useState<Meeting[]>(initialMeetings);
  const [filteredMeetings, setFilteredMeetings] = useState<Meeting[]>(initialMeetings);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    if (searchQuery.trim() === '') {
      setFilteredMeetings(meetings);
    } else {
      const filtered = meetings.filter(meeting => 
        meeting.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        meeting.organizer.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredMeetings(filtered);
    }
  }, [searchQuery, meetings]);

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleNewMeeting = () => {
    alert('새 회의 등록 기능이 구현됩니다.');
  };

  const handleRecording = () => {
    alert('실시간 녹음 기능이 구현됩니다.');
  };

  const handleWebexJoin = (meeting: Meeting) => {
    if (meeting.webexUrl) {
      window.open(meeting.webexUrl, '_blank');
    } else {
      alert('WebEx 링크가 없습니다.');
    }
  };

  const handleMinutesAction = (meeting: Meeting) => {
    if (meeting.minutesStatus === 'write') {
      alert(`${meeting.name} 회의록 작성 기능이 구현됩니다.`);
    } else {
      alert(`${meeting.name} 회의록 보기 기능이 구현됩니다.`);
    }
  };

  return (
    <div className="app">
      <Header />
      <SearchBar onSearch={handleSearch} />
      <ActionButtons 
        totalMeetings={meetings.length}
        onNewMeeting={handleNewMeeting}
        onRecording={handleRecording}
      />
      <MeetingTable 
        meetings={filteredMeetings}
        onWebexJoin={handleWebexJoin}
        onMinutesAction={handleMinutesAction}
      />
    </div>
  );
};

export default App;
