import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ActionButtons from './components/ActionButtons';
import MeetingTable from './components/MeetingTable';
import DistributeButton from './components/DistributeButton';
import MeetingRegistrationModal from './components/MeetingRegistrationModal';
import { Meeting } from './types/Meeting';
import { meetings as initialMeetings } from './data/meetings';
import './App.css';

const App: React.FC = () => {
  const [meetings, setMeetings] = useState<Meeting[]>([]); // 빈 배열로 시작
  const [filteredMeetings, setFilteredMeetings] = useState<Meeting[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMeetings, setSelectedMeetings] = useState<string[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingMeeting, setEditingMeeting] = useState<Meeting | null>(null);

  // 초기 데이터 로드
  useEffect(() => {
    setMeetings(initialMeetings);
  }, []);

  useEffect(() => {
    if (searchQuery.trim() === '') {
      setFilteredMeetings(meetings);
    } else {
      const filtered = meetings.filter(meeting => 
        meeting.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        meeting.organizer.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredMeetings(filtered);
    }
  }, [searchQuery, meetings]);

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleNewMeeting = () => {
    setEditingMeeting(null); // 새 회의 등록 모드
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
    setEditingMeeting(null); // 편집 상태 초기화
  };

  const handleMeetingEdit = (meeting: Meeting) => {
    setEditingMeeting(meeting); // 편집할 회의 설정
    setIsModalOpen(true);
  };

  const handleModalSave = (meetingData: any) => {
    // 날짜와 시간을 결합하여 DateTime 객체 생성
    const startTime = new Date(`${meetingData.date}T${meetingData.startTime}:00`);
    const endTime = new Date(`${meetingData.date}T${meetingData.endTime}:00`);
    
    if (editingMeeting) {
      // 편집 모드: 기존 회의 업데이트
      const updatedMeeting: Meeting = {
        ...editingMeeting,
        name: meetingData.meetingName,
        date: meetingData.date,
        time: `${meetingData.startTime}~${meetingData.endTime}`,
        startDateTime: startTime,
        endDateTime: endTime,
        location: meetingData.location || '장소 미정',
        organizer: {
          name: meetingData.organizer || '주관자 미정',
          title: '주관자'
        },
        attendees: meetingData.attendees ? meetingData.attendees.split(',').map((name: string) => name.trim()) : [],
        totalAttendees: meetingData.attendees ? meetingData.attendees.split(',').length + 1 : 1,
        webexUrl: meetingData.webexUrl && meetingData.webexUrl.trim() !== '' ? meetingData.webexUrl : undefined,
      };

      setMeetings(prev => prev.map(meeting => 
        meeting.id === editingMeeting.id ? updatedMeeting : meeting
      ));
      
      console.log('회의 수정:', updatedMeeting);
      alert('회의가 성공적으로 수정되었습니다.');
    } else {
      // 새 회의 등록 모드
      const newMeeting: Meeting = {
        id: Date.now().toString(), // 고유 ID 생성
        name: meetingData.meetingName,
        date: meetingData.date,
        time: `${meetingData.startTime}~${meetingData.endTime}`,
        startDateTime: startTime,
        endDateTime: endTime,
        location: meetingData.location || '장소 미정',
        organizer: {
          name: meetingData.organizer || '주관자 미정',
          title: '주관자'
        },
        attendees: meetingData.attendees ? meetingData.attendees.split(',').map((name: string) => name.trim()) : [],
        totalAttendees: meetingData.attendees ? meetingData.attendees.split(',').length + 1 : 1, // 주관자 포함
        webexUrl: meetingData.webexUrl && meetingData.webexUrl.trim() !== '' ? meetingData.webexUrl : undefined,
        status: 'scheduled',
        minutesStatus: 'write',
        hasLiveRecording: false
      };

      // 새로운 회의를 목록에 추가
      setMeetings(prev => [...prev, newMeeting]);
      
      console.log('새 회의 등록:', newMeeting);
      alert('회의가 성공적으로 등록되었습니다.');
    }
    
    setIsModalOpen(false);
    setEditingMeeting(null);
  };

  const handleWebexJoin = (meeting: Meeting) => {
    if (meeting.webexUrl) {
      window.open(meeting.webexUrl, '_blank');
    } else {
      alert('WebEx 링크가 없습니다.');
    }
  };

  const handleMinutesAction = (meeting: Meeting) => {
    if (meeting.minutesStatus === 'write') {
      alert(`${meeting.name} 회의록 작성 기능이 구현됩니다.`);
    } else {
      alert(`${meeting.name} 회의록 보기 기능이 구현됩니다.`);
    }
  };

  const handleMinutesEdit = (meeting: Meeting) => {
    setEditingMeeting(meeting);
    setIsModalOpen(true);
    // 회의록 탭으로 이동하는 로직은 모달에서 처리
  };

  const handleSaveDraft = (meetingData: any) => {
    if (editingMeeting) {
      const updatedMeeting: Meeting = {
        ...editingMeeting,
        name: meetingData.meetingName,
        date: meetingData.date,
        time: `${meetingData.startTime}~${meetingData.endTime}`,
        startDateTime: new Date(`${meetingData.date}T${meetingData.startTime}:00`),
        endDateTime: new Date(`${meetingData.date}T${meetingData.endTime}:00`),
        location: meetingData.location || '장소 미정',
        organizer: {
          name: meetingData.organizer || '주관자 미정',
          title: '주관자'
        },
        attendees: meetingData.attendees ? meetingData.attendees.split(',').map((name: string) => name.trim()) : [],
        totalAttendees: meetingData.attendees ? meetingData.attendees.split(',').length + 1 : 1,
        webexUrl: meetingData.webexUrl && meetingData.webexUrl.trim() !== '' ? meetingData.webexUrl : undefined,
        agendaItems: meetingData.agendaItems || [],
        meetingScript: meetingData.meetingScript || '',
        minutesContent: meetingData.minutesContent || '',
        minutesStatus: 'draft',
        isMinutesCompleted: false
      };

      setMeetings(prev => prev.map(meeting => 
        meeting.id === editingMeeting.id ? updatedMeeting : meeting
      ));
      
      alert('회의록이 임시 저장되었습니다.');
    }
  };

  const handleComplete = (meetingData: any) => {
    if (editingMeeting) {
      const updatedMeeting: Meeting = {
        ...editingMeeting,
        name: meetingData.meetingName,
        date: meetingData.date,
        time: `${meetingData.startTime}~${meetingData.endTime}`,
        startDateTime: new Date(`${meetingData.date}T${meetingData.startTime}:00`),
        endDateTime: new Date(`${meetingData.date}T${meetingData.endTime}:00`),
        location: meetingData.location || '장소 미정',
        organizer: {
          name: meetingData.organizer || '주관자 미정',
          title: '주관자'
        },
        attendees: meetingData.attendees ? meetingData.attendees.split(',').map((name: string) => name.trim()) : [],
        totalAttendees: meetingData.attendees ? meetingData.attendees.split(',').length + 1 : 1,
        webexUrl: meetingData.webexUrl && meetingData.webexUrl.trim() !== '' ? meetingData.webexUrl : undefined,
        agendaItems: meetingData.agendaItems || [],
        meetingScript: meetingData.meetingScript || '',
        minutesContent: meetingData.minutesContent || '',
        minutesStatus: 'view',
        isMinutesCompleted: true
      };

      setMeetings(prev => prev.map(meeting => 
        meeting.id === editingMeeting.id ? updatedMeeting : meeting
      ));
      
      alert('회의록이 완료되었습니다.');
      setIsModalOpen(false);
      setEditingMeeting(null);
    }
  };

  const handleMeetingSelect = (meetingId: string, isSelected: boolean) => {
    if (isSelected) {
      setSelectedMeetings(prev => [...prev, meetingId]);
    } else {
      setSelectedMeetings(prev => prev.filter(id => id !== meetingId));
    }
  };

  // 회의록 내보내기 기능
  const handleDistribute = () => {
    const selectedMeetingNames = filteredMeetings
      .filter(meeting => selectedMeetings.includes(meeting.id))
      .map(meeting => meeting.name);
    
    // 선택된 회의들의 회의록 상태를 'view'로 변경
    setMeetings(prev => prev.map(meeting => 
      selectedMeetings.includes(meeting.id) 
        ? { ...meeting, minutesStatus: 'view' as const }
        : meeting
    ));
    
    alert(`선택된 회의의 회의록을 배포합니다:\n${selectedMeetingNames.join('\n')}\n\n회의록 상태가 '보기'로 변경되었습니다.`);
    
    // 선택 해제
    setSelectedMeetings([]);
  };

  // 회의 삭제 기능
  const handleDelete = () => {
    const selectedMeetingNames = filteredMeetings
      .filter(meeting => selectedMeetings.includes(meeting.id))
      .map(meeting => meeting.name);
    
    const confirmDelete = window.confirm(
      `선택된 회의를 삭제하시겠습니까?\n\n${selectedMeetingNames.join('\n')}\n\n이 작업은 되돌릴 수 없습니다.`
    );
    
    if (confirmDelete) {
      // 선택된 회의들을 제거
      setMeetings(prev => prev.filter(meeting => !selectedMeetings.includes(meeting.id)));
      
      alert(`선택된 회의가 삭제되었습니다:\n${selectedMeetingNames.join('\n')}`);
      
      // 선택 해제
      setSelectedMeetings([]);
    }
  };

  return (
    <div className="app">
      <Header />
      <ActionButtons 
        totalMeetings={meetings.length}
        onNewMeeting={handleNewMeeting}
        onSearch={handleSearch}
      />
      <MeetingTable 
        meetings={filteredMeetings}
        selectedMeetings={selectedMeetings}
        onMeetingSelect={handleMeetingSelect}
        onWebexJoin={handleWebexJoin}
        onMinutesAction={handleMinutesAction}
        onMeetingEdit={handleMeetingEdit}
      />
      <DistributeButton 
        selectedCount={selectedMeetings.length}
        onDistribute={handleDistribute}
        onDelete={handleDelete}
      />
      <MeetingRegistrationModal
        isOpen={isModalOpen}
        onClose={handleModalClose}
        onSave={handleModalSave}
        editingMeeting={editingMeeting}
      />
    </div>
  );
};

export default App;
