import React, { useState } from 'react';
import './MeetingRegistrationModal.css';

interface MeetingRegistrationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (meetingData: any) => void;
}

interface Contact {
  id: string;
  name: string;
  email: string;
  department: string;
  title: string;
}

const MeetingRegistrationModal: React.FC<MeetingRegistrationModalProps> = ({
  isOpen,
  onClose,
  onSave
}) => {
  const [activeTab, setActiveTab] = useState<'basic' | 'agenda'>('basic');
  const [showOrganizerDropdown, setShowOrganizerDropdown] = useState(false);
  const [showAttendeesDropdown, setShowAttendeesDropdown] = useState(false);
  const [organizerSearch, setOrganizerSearch] = useState('');
  const [attendeesSearch, setAttendeesSearch] = useState('');
  
  const [formData, setFormData] = useState({
    // 기본 정보
    meetingName: '',
    date: '',
    startTime: '',
    endTime: '',
    location: '',
    organizer: '',
    attendees: '',
    
    // Webex 정보
    webexUrl: 'https://company.webex.com/meet/...',
    meetingKey: '',
    meetingPassword: '',
    
    // 안건 정보
    background: '',
    terminology: '용어: 정의\nMVP: Minimum Viable Product\nKPI: Key Performance Indicator'
  });

  // 주소록 데이터 (실제로는 API에서 가져올 수 있음)
  const contacts: Contact[] = [
    { id: '1', name: '김철수', email: 'kim@company.com', department: '제품기획팀', title: '팀장' },
    { id: '2', name: '박지은', email: 'park@company.com', department: '마케팅팀', title: '부장' },
    { id: '3', name: '서지훈', email: 'seo@company.com', department: '개발팀', title: '책임' },
    { id: '4', name: '이미영', email: 'lee@company.com', department: '디자인팀', title: '대리' },
    { id: '5', name: '정태호', email: 'jung@company.com', department: '영업팀', title: '과장' },
    { id: '6', name: '한소영', email: 'han@company.com', department: '인사팀', title: '사원' },
    { id: '7', name: '조현우', email: 'cho@company.com', department: 'AI연구소', title: '연구원' },
    { id: '8', name: '김민수', email: 'kim2@company.com', department: '보안팀', title: '팀장' }
  ];

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSave = () => {
    onSave(formData);
    onClose();
  };

  const handleDateChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    handleInputChange('date', e.target.value);
  };

  const handleTimeChange = (field: 'startTime' | 'endTime', value: string) => {
    // 24시간 형식으로 변환
    if (value) {
      const [hours, minutes] = value.split(':');
      const hour24 = parseInt(hours);
      const formattedTime = `${hours.padStart(2, '0')}:${minutes}`;
      handleInputChange(field, formattedTime);
    } else {
      handleInputChange(field, value);
    }
  };

  const handleOrganizerSelect = (contact: Contact) => {
    handleInputChange('organizer', `${contact.name} (${contact.department})`);
    setOrganizerSearch('');
    setShowOrganizerDropdown(false);
  };

  const handleAttendeeSelect = (contact: Contact) => {
    const currentAttendees = formData.attendees ? formData.attendees.split(',').map(s => s.trim()) : [];
    if (!currentAttendees.includes(contact.name)) {
      const newAttendees = [...currentAttendees, contact.name].join(', ');
      handleInputChange('attendees', newAttendees);
    }
    setAttendeesSearch('');
    setShowAttendeesDropdown(false);
  };

  const filteredOrganizers = contacts.filter(contact =>
    contact.name.toLowerCase().includes(organizerSearch.toLowerCase()) ||
    contact.department.toLowerCase().includes(organizerSearch.toLowerCase())
  );

  const filteredAttendees = contacts.filter(contact =>
    contact.name.toLowerCase().includes(attendeesSearch.toLowerCase()) ||
    contact.department.toLowerCase().includes(attendeesSearch.toLowerCase())
  );

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        <div className="modal-header">
          <h2 className="modal-title">새 회의 등록</h2>
          <div className="modal-actions">
            <button className="btn btn-primary" onClick={handleSave}>
              <i className="fas fa-save"></i>
              저장
            </button>
            <button className="btn btn-cancel" onClick={onClose}>
              <i className="fas fa-times"></i>
              취소
            </button>
          </div>
        </div>

        <div className="modal-tabs">
          <button 
            className={`tab-button ${activeTab === 'basic' ? 'active' : ''}`}
            onClick={() => setActiveTab('basic')}
          >
            <i className="fas fa-info-circle"></i>
            기본 정보
          </button>
          <button 
            className={`tab-button ${activeTab === 'agenda' ? 'active' : ''}`}
            onClick={() => setActiveTab('agenda')}
          >
            <i className="fas fa-list-alt"></i>
            회의 Agenda
          </button>
        </div>

        <div className="modal-content">
          {activeTab === 'basic' ? (
            // 기본 정보 탭
            <div className="form-section">
              <div className="form-group">
                <label className="form-label">회의명 *</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="회의 이름을 입력하세요"
                  value={formData.meetingName}
                  onChange={(e) => handleInputChange('meetingName', e.target.value)}
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">회의일자 *</label>
                  <div className="input-with-icon">
                    <input
                      type="date"
                      className="form-input"
                      value={formData.date}
                      onChange={handleDateChange}
                    />
                    <i className="fas fa-calendar input-icon"></i>
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">시작 시간 *</label>
                  <input
                    type="time"
                    className="form-input"
                    placeholder="00:00"
                    value={formData.startTime}
                    onChange={(e) => handleTimeChange('startTime', e.target.value)}
                    step="900"
                    min="00:00"
                    max="23:59"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">종료 시간 *</label>
                  <input
                    type="time"
                    className="form-input"
                    placeholder="00:00"
                    value={formData.endTime}
                    onChange={(e) => handleTimeChange('endTime', e.target.value)}
                    step="900"
                    min="00:00"
                    max="23:59"
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">장소</label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="회의실 이름 또는 주소"
                  value={formData.location}
                  onChange={(e) => handleInputChange('location', e.target.value)}
                />
              </div>

              <div className="form-group">
                <label className="form-label">주관자 *</label>
                <div className="dropdown-container">
                  <input
                    type="text"
                    className="form-input"
                    placeholder="주관자 검색 또는 직접 입력"
                    value={organizerSearch}
                    onChange={(e) => setOrganizerSearch(e.target.value)}
                    onFocus={() => setShowOrganizerDropdown(true)}
                  />
                  {showOrganizerDropdown && (
                    <div className="dropdown-list">
                      {filteredOrganizers.map(contact => (
                        <div
                          key={contact.id}
                          className="dropdown-item"
                          onClick={() => handleOrganizerSelect(contact)}
                        >
                          <div className="contact-name">{contact.name}</div>
                          <div className="contact-info">{contact.department} • {contact.title}</div>
                        </div>
                      ))}
                    </div>
                  )}
                  {formData.organizer && (
                    <div className="selected-value">
                      <span>{formData.organizer}</span>
                      <button
                        className="clear-button"
                        onClick={() => handleInputChange('organizer', '')}
                      >
                        <i className="fas fa-times"></i>
                      </button>
                    </div>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">참석자</label>
                <div className="dropdown-container">
                  <input
                    type="text"
                    className="form-input"
                    placeholder="참석자 검색 (쉼표로 구분)"
                    value={attendeesSearch}
                    onChange={(e) => setAttendeesSearch(e.target.value)}
                    onFocus={() => setShowAttendeesDropdown(true)}
                  />
                  {showAttendeesDropdown && (
                    <div className="dropdown-list">
                      {filteredAttendees.map(contact => (
                        <div
                          key={contact.id}
                          className="dropdown-item"
                          onClick={() => handleAttendeeSelect(contact)}
                        >
                          <div className="contact-name">{contact.name}</div>
                          <div className="contact-info">{contact.department} • {contact.title}</div>
                        </div>
                      ))}
                    </div>
                  )}
                  {formData.attendees && (
                    <div className="selected-attendees">
                      {formData.attendees.split(',').map((attendee, index) => (
                        <span key={index} className="attendee-tag">
                          {attendee.trim()}
                          <button
                            className="remove-attendee"
                            onClick={() => {
                              const attendees = formData.attendees.split(',').filter((_, i) => i !== index);
                              handleInputChange('attendees', attendees.join(', '));
                            }}
                          >
                            <i className="fas fa-times"></i>
                          </button>
                        </span>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div className="form-section-title">Webex 회의 정보</div>
              
              <div className="form-group">
                <label className="form-label">Join URL</label>
                <input
                  type="text"
                  className="form-input"
                  value={formData.webexUrl}
                  onChange={(e) => handleInputChange('webexUrl', e.target.value)}
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label className="form-label">Meeting Key</label>
                  <input
                    type="text"
                    className="form-input"
                    placeholder="회의 번호 또는 ID"
                    value={formData.meetingKey}
                    onChange={(e) => handleInputChange('meetingKey', e.target.value)}
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">Meeting Password</label>
                  <input
                    type="text"
                    className="form-input"
                    placeholder="회의 비밀번호"
                    value={formData.meetingPassword}
                    onChange={(e) => handleInputChange('meetingPassword', e.target.value)}
                  />
                </div>
              </div>
            </div>
          ) : (
            // 회의 Agenda 탭
            <div className="form-section">
              <div className="agenda-header">
                <h3>회의 안건</h3>
                <button className="btn btn-secondary btn-sm">
                  <i className="fas fa-plus"></i>
                  안건 추가
                </button>
              </div>

              <div className="form-group">
                <label className="form-label">회의 배경 / 목적</label>
                <textarea
                  className="form-textarea"
                  placeholder="회의를 개최하는 이유와 달성하고자 하는 목표를 입력하세요"
                  value={formData.background}
                  onChange={(e) => handleInputChange('background', e.target.value)}
                  rows={6}
                />
              </div>

              <div className="form-group">
                <label className="form-label">주요 전문 용어</label>
                <textarea
                  className="form-textarea"
                  value={formData.terminology}
                  onChange={(e) => handleInputChange('terminology', e.target.value)}
                  rows={6}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MeetingRegistrationModal;
